INSERT INTO locations (name, theme, description, image_path) VALUES
                                                                 ('Ancient Castle', 'Gothic', 'Величественный готический замок, скрывающий множество тайн', 'assets/locations/location_1.png'),
                                                                 ('Order Dungeon', 'Mystic', 'Мистическое подземелье, пропитанное магией и древними легендами', 'assets/locations/location_2.png'),
                                                                 ('Forgotten City', 'Decay', 'Заброшенный город, где время остановилось столетия назад', 'assets/locations/location_3.png'),
                                                                 ('Shadow Forest', 'Nature', 'Дремучий лес, где деревья видели эпохи, скрывая их секреты в своих ветвях', 'assets/locations/location_4.png'),
                                                                 ('Crystal Palace', 'Fantasy', 'Волшебный дворец, сияющий всеми цветами радуги, воплощение магии и красоты', 'assets/locations/location_5.png');
INSERT INTO room_descriptions (location_id, room_number, description, mood) VALUES
                                                                                (1, 1, '[Замок] Вы входите в величественный главный зал. Мощные каменные стены возвышаются, поддерживаемые толстыми колоннами. На полу видны остатки былого величия - истертый паркет и разбитая мозаика.', 'dark'),
                                                                                (1, 2, '[Замок] Извилистая каменная лестница ведет вверх в полумрак. Каждый шаг издает глухой звук, эхо разносится по залам. Где-то вдалеке слышны странные звуки.', 'mysterious'),
                                                                                (1, 3, '[Замок] Вы находитесь в библиотеке замка. Книжные полки, пропитанные влагой, угрожают обрушиться под тяжестью знаний. Запах старой бумаги наполняет воздух.', 'gloomy'),
                                                                                (1, 4, '[Замок] Коридор украшен древними гобеленами, которые почти рассыпаются при малейшем дуновении ветра. Портреты длинных умерших владельцев смотрят на вас пустыми глазами.', 'gothic'),
                                                                                (1, 5, '[Замок] Вы входите в бальный зал. Хрустальная люстра, потеряв большую часть своих украшений, едва держится на цепях. На паркете видны следы последнего танца.', 'dark'),
                                                                                (1, 6, '[Замок] Узкий коридор с каменными стенами, почти полностью покрытыми мхом. Влага от стен капает на пол с глухим звуком капельницы времени.', 'mysterious'),
                                                                                (1, 7, '[Замок] Часовня замка встречает вас холодом и тишиной. Алтарь давно никого не видел. На полу лежат фрагменты витражного стекла, переливаясь в свете луны.', 'gloomy'),
                                                                                (1, 8, '[Замок] Кухня замка заросла странными растениями. Кованые горшки все еще висят на стенах, словно ожидая последнего приготовления.', 'gothic'),
                                                                                (1, 9, '[Замок] Вы в тронном зале. Пустой трон возвышается в центре комнаты, накапливая пыль веков. Скелеты свечей охраняют его молчание.', 'dark'),
                                                                                (1, 10, '[Замок] Подземелье замка. Кольца для цепей видны на стенах. Воздух здесь кажется живым, полным шепотов прошлого. Путь к выходу лежит впереди.', 'mysterious');
INSERT INTO room_descriptions (location_id, room_number, description, mood) VALUES
                                                                                (2, 1, '[Подземелье] Вы спускаетесь в темноту. Влага от стен капает на пол, создавая глухой ритм времени. Воздух становится все более тяжелым с каждым шагом.', 'oppressive'),
                                                                                (2, 2, '[Подземелье] Извилистые коридоры подземелья манят вас в неизвестность. На стенах видна непонятная разметка - может быть, карта или проклятие?', 'mystical'),
                                                                                (2, 3, '[Подземелье] Пещера с кристаллами, светящимися изнутри слабым голубым светом. Звук кристаллов наполняет воздух невидимой музыкой.', 'sacred'),
                                                                                (2, 4, '[Подземелье] Озеро подземной воды отражает свет факелов. На поверхности видны легкие ряби, как будто кто-то недавно переплывал его.', 'cold'),
                                                                                (2, 5, '[Подземелье] Алтарь из черного камня возвышается перед вами. Вокруг него разбросаны цветы, давно высохшие, но все еще сохраняющие острый запах.', 'oppressive'),
                                                                                (2, 6, '[Подземелье] Комната, полная гробниц. На крышках видны имена, стертые временем. Ощущение, что вы нарушили чей-то вечный сон.', 'mystical'),
                                                                                (2, 7, '[Подземелье] Коридор усыпан древними монетами. Они светят в полумраке, словно приманивая вас в ловушку.', 'sacred'),
                                                                                (2, 8, '[Подземелье] Комната с рунами на стенах. Они светятся при вашем прохождении, словно узнавая вас. Магия здесь по-прежнему очень сильна.', 'cold'),
                                                                                (2, 9, '[Подземелье] Лабиринт из черного камня. На каждом повороте вы видите новое отражение себя. Реальность здесь кажется зыбкой.', 'oppressive'),
                                                                                (2, 10, '[Подземелье] Большой подземный зал с потолком, теряющимся в темноте. В центре - портал, излучающий слабый свет. Выход? Или ловушка?', 'mystical');
INSERT INTO room_descriptions (location_id, room_number, description, mood) VALUES
                                                                                (3, 1, '[Город] Руины дворца встают перед вами. Развалины архитектуры, когда-то величественной, теперь окутаны лозами и мхом.', 'desolate'),
                                                                                (3, 2, '[Город] Главная площадь города. Статуи, их лица стертые временем, как будто забыли имена тех, кого изображали.', 'abandoned'),
                                                                                (3, 3, '[Город] Рыночные прилавки, где когда-то кипела жизнь, теперь представляют собой груды дерева и металла. Запах гниения смешивается с пылью.', 'decaying'),
                                                                                (3, 4, '[Город] Библиотека города. Книги разбросаны по полу, их страницы скручены влагой. Знания, которые они когда-то содержали, теперь потеряны.', 'forgotten'),
                                                                                (3, 5, '[Город] Жилые кварталы, где дома стоят пусто, их окна как пустые глазницы. На улицах ни единого признака жизни.', 'desolate'),
                                                                                (3, 6, '[Город] Замок города, частично обрушившийся. Через трещины видны комнаты, доступные только воспоминаниям.', 'abandoned'),
                                                                                (3, 7, '[Город] Сад, где когда-то цветы радовали своей красотой. Теперь здесь растут только сорняки и дикие растения.', 'decaying'),
                                                                                (3, 8, '[Город] Храм города. Алтарь покрыт мхом, но вы все еще чувствуете здесь присутствие чего-то священного.', 'forgotten'),
                                                                                (3, 9, '[Город] Водопровод города, давно иссохший. Но где-то в его глубинах вы слышите звук воды - может быть, город еще не полностью мертв?', 'desolate'),
                                                                                (3, 10, '[Город] Акрополь города. С вершины видны все развалины. Время здесь остановилось, оставив вас одного перед массивом прошлого.', 'abandoned');
INSERT INTO room_descriptions (location_id, room_number, description, mood) VALUES
                                                                                (4, 1, '[Лес] Вы входите в густой лес. Ветви деревьев переплетаются над головой, создавая естественный свод. Свет едва проникает сквозь листву.', 'wild'),
                                                                                (4, 2, '[Лес] Полумрак царит в чаще леса. Запах хвои и гнилого дерева наполняет ноздри. Листья шелестят под ногами.', 'natural'),
                                                                                (4, 3, '[Лес] Древний лес - деревья здесь поражают своей высотой и возрастом. Они видели столетия и кажутся хранителями времени.', 'ancient'),
                                                                                (4, 4, '[Лес] Ручей пробивает себе путь сквозь лес. Его журчание - единственный звук, который нарушает тишину природы.', 'dangerous'),
                                                                                (4, 5, '[Лес] Поляна, заросшая странными грибами. Они светят в полумраке, привлекая вас на явно опасную территорию.', 'wild'),
                                                                                (4, 6, '[Лес] Деревья здесь так близко стоят друг к другу, что образуют лабиринт. Ориентация становится все более сложной.', 'natural'),
                                                                                (4, 7, '[Лес] Место силы - большой камень в центре поляны. На нем видны потертые символы древней магии.', 'ancient'),
                                                                                (4, 8, '[Лес] Заброшенный охотничий домик, полуразрушенный и покрытый плющом. Внутри видны следы прошлых обитателей.', 'dangerous'),
                                                                                (4, 9, '[Лес] Деревья образуют коридор, ведущий куда-то вглубь леса. Ощущение, что лес активно направляет вас в определенное место.', 'wild'),
                                                                                (4, 10, '[Лес] Открытое место. Лес как будто отпускает вас. Впереди видны светлые просветы - возможно, выход из лабиринта деревьев.', 'natural');
INSERT INTO room_descriptions (location_id, room_number, description, mood) VALUES
                                                                                (5, 1, '[Дворец] Вы входите в сияющий дворец. Кристаллы светят из каждого уголка, создавая калейдоскоп цветов вокруг вас.', 'magical'),
                                                                                (5, 2, '[Дворец] Залы дворца переливаются светом. Каждая грань кристалла преломляет свет, создавая оптические иллюзии.', 'beautiful'),
                                                                                (5, 3, '[Дворец] Звон кристаллов наполняет воздух. Музыка, которую вы слышите, кажется, исходит изнутри вашей собственной сущности.', 'dazzling'),
                                                                                (5, 4, '[Дворец] Тронный зал дворца. Кристальный трон возвышается, светя интенсивным светом. Ощущение присутствия чего-то могущественного.', 'ethereal'),
                                                                                (5, 5, '[Дворец] Библиотека дворца. Книги, покрытые прозрачными кристаллами, хранят знания вселенной. Каждая страница светит собственным светом.', 'magical'),
                                                                                (5, 6, '[Дворец] Сад дворца. Цветы здесь сделаны из кристаллов. Они цветут в свете, создавая естественный пейзаж красоты.', 'beautiful'),
                                                                                (5, 7, '[Дворец] Залы отражений. Везде видны отражения - вы видите себя бесконечное количество раз, словно путешествуя между измерениями.', 'dazzling'),
                                                                                (5, 8, '[Дворец] Комната музыки. Кристаллы издают гармоничные звуки при малейшем движении воздуха. Это место кажется источником всей музыки мира.', 'ethereal'),
                                                                                (5, 9, '[Дворец] Башня дворца. С вершины можно увидеть весь кристальный лабиринт внизу. Магия здесь достигает пика своей силы.', 'magical'),
                                                                                (5, 10, '[Дворец] Центр дворца. Массивный кристалл в центре комнаты светит всеми цветами радуги. Это сердце дворца, источник всей его магии.', 'beautiful');
INSERT INTO riddles (question, answer, difficulty, category) VALUES
                                                                 ('Как нужно смотреть на врага?', 'внимательно', 1, 'geography'),
                                                                 ('Что можно приготовить, но нельзя съесть?', 'уроки', 1, 'nature'),
                                                                 ('Всегда шагают друг за другом, но никогда не встречаются.', 'день и ночь', 2, 'logic'),
                                                                 ('Белое поле, чёрные семена. Кто их сеет, тот разум имеет.', 'книга', 2, 'animals'),
                                                                 ('Зимой и летом одним цветом.', 'ёлка', 2, 'riddles'),
                                                                 ('Маленький, серенький, по полю прыг-скок, в зубах несёт колосок.', 'мышь', 1, 'geography'),
                                                                 ('Дом стеклянный, дом прозрачный, дом без дверей, без окошек, а жильцов в нём — невпроворот.', 'аквариум', 2, 'objects'),
                                                                 ('Сидит дед, во сто шуб одет, кто его раздевает — слёзы проливает.', 'лук', 2, 'math'),
                                                                 ('Висит груша — нельзя скушать.', 'лампочка', 2, 'objects'),
                                                                 ('Летом серый, зимой белый, длинноухий и пугливый.', 'заяц', 1, 'astronomy'),
                                                                 ('Всех встречает, провожает, а сама с места не сходит.', 'дверь', 1, 'grammar'),
                                                                 ('Не зверь, не птица, нос как спица, летит — кричит, сядет — молчит.', 'комар', 1, 'nature'),
                                                                 ('Я хожу, но не двигаюсь. Что это?', 'часы', 1, 'objects'),
                                                                 ('Без рук, без ног, а ворота открывает.', 'ветер', 1, 'objects'),
                                                                 ('Я летаю, но не птица. Что это?', 'самолет', 1, 'transport'),
                                                                 ('Идёт, идёт, а не человек, свет да тьму несёт вовек.', 'солнце', 1, 'nature'),
                                                                 ('Хвост пушистый, мех золотистый, в лесу живёт, кур в деревне крадёт.', 'лиса', 2, 'logic'),
                                                                 ('Сто одежек и все без застёжек.', 'капуста', 1, 'objects'),
                                                                 ('Летом в лесу обитает, зимой в берлоге спит.', 'медведь', 1, 'objects'),
                                                                 ('Сам алый, сахарный, кафтан зелёный, бархатный.', 'арбуз', 1, 'calendar'),
                                                                 ('Я могу быть и жидким, и твердым, и газом. Что это?', 'вода', 1, 'nature'),
                                                                 ('Без ног, без крыльев оно — быстро летит, не догонишь его.', 'время', 1, 'abstract'),
                                                                 ('По небу гуляет, всех солнца лишает.', 'облако', 2, 'logic'),
                                                                 ('Без языка, а говорит, без крыльев, а летит.', 'письмо', 1, 'objects'),
                                                                 ('Не огонь, а жжётся, не солнце, а светит, не небо, а высоко.', 'крапива', 1, 'logic'),
                                                                 ('Мягкий, пушистый, из трубочки свисает, зимой всех согревает.', 'шарф', 2, 'riddles'),
                                                                 ('Днём спит, ночью летает, прохожих пугает, мышей добывает.', 'сова', 1, 'objects'),
                                                                 ('Без рук, без ног, а рисовать умеет зимний лес на всех окнах.', 'мороз', 1, 'logic'),
                                                                 ('В землю влез, хвостом тряс, солнце увидел — обратно залез.', 'морковь', 1, 'nature'),
                                                                 ('По небу плывёт, солнце иногда спрячёт, дождь с неё польётся.', 'туча', 2, 'riddles');
INSERT INTO notes (content, location_id, rarity) VALUES
                                                     ('На стене замка гравировка: "Помни о древних, когда солнце и луна встречаются."', 1, 'COMMON'),
                                                     ('Фрагмент пергамента: "Золото не самое ценное в этих стенах. Истина - вот настоящий клад."', 1, 'IMPORTANT'),
                                                     ('Странная фраза: "Три двери слева, одна справа, и выход найдешь."', 1, 'COMMON'),
                                                     ('Письмо: "Я спрятал секрет там, где никто не посмотрит... под полом тронного зала."', 1, 'SECRET'),
                                                     ('Запись голоса: "Помощник, если ты это читаешь, отступи назад."', 1, 'COMMON'),
                                                     ('Странный рисунок с надписью: "Путь мудреца - путь вопросов."', 1, 'IMPORTANT'),
                                                     ('Цитата: "В замке каждая комната имеет свой дух, уважай его."', 1, 'COMMON'),
                                                     ('Нотатка: "Выход находится не там, где кажется. Ищи отражение."', 1, 'SECRET'),
                                                     ('Засушенный цветок с биркой: "От моей любви, которая никогда не вернется."', 1, 'COMMON'),
                                                     ('Коротко: "Не верь звукам в коридоре."', 1, 'IMPORTANT'),
                                                     ('В подземелье слышны шепоты. "Золотой ключ охраняется древним духом."', 2, 'IMPORTANT'),
                                                     ('На стене: "Три и три равно шести, но шесть и шесть не равно двенадцати здесь."', 2, 'COMMON'),
                                                     ('Записка: "Кристаллы подземелья - это ключи к другому миру."', 2, 'SECRET'),
                                                     ('Голос в пещере: "Тот, кто говорит загадкой, должен ответить на загадку."', 2, 'COMMON'),
                                                     ('Объявление: "ОСТОРОЖНО! Озеро подземелья живое!"', 2, 'IMPORTANT'),
                                                     ('Письмо: "Я потерял путь в этом лабиринте. Если ты это читаешь, я уже мертв."', 2, 'COMMON'),
                                                     ('Мудрость: "В подземелье время течет по-другому."', 2, 'SECRET'),
                                                     ('Совет: "Не слушай голоса в темноте."', 2, 'COMMON'),
                                                     ('Фраза: "Здесь живет древний страж. Уважь его границы."', 2, 'IMPORTANT'),
                                                     ('Молитва: "Пусть кристаллы укажут путь истинный."', 2, 'COMMON'),
                                                     ('Руины города видели падение цивилизации. "Не совершай их ошибок."', 3, 'IMPORTANT'),
                                                     ('На развалинах: "Город был велик, но гордыня его погубила."', 3, 'COMMON'),
                                                     ('Нотатка путешественника: "Этот город требует уважения к истории."', 3, 'COMMON'),
                                                     ('Надпись на камне: "Разрушенный трон ждет нового владельца."', 3, 'SECRET'),
                                                     ('Письмо: "Я остался в городе слишком долго. Он меня изменил."', 3, 'IMPORTANT'),
                                                     ('На площади: "Статуи помнят живых, которые их создали."', 3, 'COMMON'),
                                                     ('Совет: "В руинах города спрятаны и сокровища, и проклятия."', 3, 'COMMON'),
                                                     ('Мудрость: "История защищает тех, кто ее уважает."', 3, 'SECRET'),
                                                     ('Фраза: "Где-то в развалинах есть живая вода, которая оживляет все."', 3, 'IMPORTANT'),
                                                     ('Последняя запись городского архива: "Кто найдет эту записку, знай, что город ждет спасения."', 3, 'COMMON'),
                                                     ('В лесу шорохи: "Деревья видели империи встать и упасть."', 4, 'IMPORTANT'),
                                                     ('На коре дерева вырезано: "Лес - это дом для тех, кто потерялся в жизни."', 4, 'COMMON'),
                                                     ('Совет охотника: "В лесу не будьте гордыней, будьте смиренными."', 4, 'COMMON'),
                                                     ('Мудрость природы: "Каждое дерево - это книга, если уметь читать."', 4, 'SECRET'),
                                                     ('Записка: "Я встретил в лесу существо, которое было старше деревьев."', 4, 'IMPORTANT'),
                                                     ('На камне: "Ручей ведет к сердцу леса."', 4, 'COMMON'),
                                                     ('Предупреждение: "Не заходите в глубокий лес ночью."', 4, 'COMMON'),
                                                     ('Фраза: "Лес выбирает, кого пропустить, а кого задержать."', 4, 'SECRET'),
                                                     ('В дневнике: "Я нашел древний храм в лесу. Он выглядит живым."', 4, 'IMPORTANT'),
                                                     ('Последний крик: "Лес поглотил меня, но я счастлив остаться."', 4, 'COMMON'),
                                                     ('Во дворце: "Кристаллы содержат воспоминания вселенной."', 5, 'SECRET'),
                                                     ('На алтаре: "Свет кристаллов показывает истину для избранных."', 5, 'IMPORTANT'),
                                                     ('Записка магия: "Дворец обитаем силами света и добра."', 5, 'COMMON'),
                                                     ('Совет колдуна: "Слушай музыку кристаллов - она расскажет правду."', 5, 'COMMON'),
