-- @language SQLite
-- Удаляем старые таблицы
DROP TABLE IF EXISTS found_notes;
DROP TABLE IF EXISTS gamesaves;
DROP TABLE IF EXISTS notes;
DROP TABLE IF EXISTS items;
DROP TABLE IF EXISTS riddles;
DROP TABLE IF EXISTS room_descriptions;
DROP TABLE IF EXISTS locations;

-- ============================================
-- ОСНОВНЫЕ ТАБЛИЦЫ
-- ============================================

-- Таблица локаций
CREATE TABLE locations (
                           id INTEGER PRIMARY KEY AUTOINCREMENT,
                           name TEXT NOT NULL UNIQUE,
                           theme TEXT,
                           description TEXT,
                           image_path TEXT,  -- путь к изображению локации
                           created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Таблица описаний комнат (10 описаний на локацию)
CREATE TABLE room_descriptions (
                                   id INTEGER PRIMARY KEY AUTOINCREMENT,
                                   location_id INTEGER NOT NULL,
                                   room_number INTEGER NOT NULL,  -- 1-10
                                   description TEXT NOT NULL,
                                   mood TEXT,
                                   created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                                   FOREIGN KEY(location_id) REFERENCES locations(id)
);

-- Таблица загадок (расширенная - 50+ загадок)
CREATE TABLE riddles (
                         id INTEGER PRIMARY KEY AUTOINCREMENT,
                         question TEXT NOT NULL,
                         answer TEXT NOT NULL,
                         difficulty INTEGER DEFAULT 1,  -- 1=easy, 2=medium, 3=hard
                         category TEXT,  -- тип загадки
                         created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Таблица записок (расширенная - 100+ записок)
CREATE TABLE notes (
                       id INTEGER PRIMARY KEY AUTOINCREMENT,
                       content TEXT NOT NULL,
                       location_id INTEGER,
                       rarity TEXT DEFAULT 'COMMON',  -- COMMON, IMPORTANT, SECRET
                       created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                       FOREIGN KEY(location_id) REFERENCES locations(id)
);

-- Таблица предметов
CREATE TABLE items (
                       id INTEGER PRIMARY KEY AUTOINCREMENT,
                       type TEXT NOT NULL CHECK(type IN ('GOLD_KEY', 'SILVER_KEY')),
                       name TEXT,
                       description TEXT,
                       rarity TEXT DEFAULT 'COMMON',  -- COMMON, RARE, LEGENDARY
                       icon_path TEXT  -- путь к иконке
);

-- Таблица для отслеживания найденных записок
CREATE TABLE found_notes (
                             id INTEGER PRIMARY KEY AUTOINCREMENT,
                             game_session INTEGER,
                             note_id INTEGER NOT NULL,
                             found_at_location INTEGER,
                             found_at_room INTEGER,
                             found_at_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                             FOREIGN KEY(note_id) REFERENCES notes(id)
);

-- Таблица сохранений игры
CREATE TABLE gamesaves (
                           id INTEGER PRIMARY KEY AUTOINCREMENT,
                           player_name TEXT,
                           gold_bars INTEGER DEFAULT 0,
                           current_location INTEGER DEFAULT 0,
                           current_room INTEGER DEFAULT 0,
                           inventory TEXT,  -- JSON
                           logs TEXT,  -- JSON
                           notes_found TEXT,  -- JSON
                           saved_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

